import request from '@/utils/request'

const baseUrl = `/log/warehouse/goods`

export default {
    pageFind(page, limit, goodsQueryVo) {  // 分页查询商品信息
        return request({
            url: `${baseUrl}/pageFind/${page}/${limit}`,
            method: 'post',
            data: goodsQueryVo
        })
    },

    add(goodsVo) {
        return request({
            url: `${baseUrl}/add`,
            method: 'post',
            data: goodsVo
        })
    }
}
