// 引入 axios
import axios from 'axios'

const service = axios.create({
    baseURL: 'http://192.168.195.130',
    timeout: 15000 // 请求超时时间
})

// 设置响应接口
axios.interceptors.response.use(success => {  // 访问到了后端的接口，需要判断是否逻辑正确

    if (success.status && success.status === 200) {
        return
    }

    return success.data  // 正常接收到结构 需要将对象返回
}, error => {   // 直接错误响应，直接服务器都不让进行访问

    console.log(error.url)

    if (error.response.code === 500) {
        // 对应提示服务器错误
    }
    // ...

    return
})

axios.interceptors.request.use(success => {
    // 请求前查询是否包含 token 如果有 token 则携带 token
    this.$message.success('token = ' + window.sessionStorage.getItem('token'))
    if (window.sessionStorage.getItem('token')) {
        this.$message.success('token = ' + window.sessionStorage.getItem('token'))
        success.headers['Authorization'] = window.sessionStorage.getItem('token')
    }

}, error => {
})

export default service
