import request from '@/utils/request'
import ca from "element-ui/src/locale/lang/ca";

const baseUrl = `/log/car/car`

export default {
    getCar() {    // 获取车辆信息
        return request({
            url: `${baseUrl}/getCar`,
            method: 'get'
        })
    },

    dissolveRelationship(carId) {  // 司机解除其和车辆的关系
        return request({
            url: `${baseUrl}/dissolveRelationship/${carId}`,
            method: 'put'
        })
    },

    pageFind(page, limit, carPageParams) {   // 分页查找车辆信息
        return request({
            url: `${baseUrl}/pageFind/${page}/${limit}`,
            method: 'post',
            data: carPageParams
        })
    }
}
