import Vue from 'vue'
import VueRouter from 'vue-router'
import Home from "@/views/Home"
import Login from "@/views/Login"
import Goods from "@/views/warehouse/Goods"
import InOrder from "@/views/warehouse/InOrder"
import Car from "@/views/car/Car"
import CarRepair from "@/views/car/CarRepair"
import Cmn from "@/views/cmn/Cmn"
import Order from "@/views/order/Order"
import OrderHistory from "@/views/order/OrderHistory"
import CarManage from "@/views/dispatcher/CarManage";
import OrderPlat from "@/views/dispatcher/OrderPlat";
import PersonOrder from "@/views/dispatcher/PersonOrder";
import HistoryOrder from "@/views/dispatcher/HistoryOrder";

Vue.use(VueRouter)

const routes = [
    {
        path: '/home',
        name: 'home',
        component: Home,
        children: [
            {
                path: '/warehouse/goods',
                name: 'goods',
                component: Goods  // 商品管理
            },
            {
                path: '/warehouse/inOrder',
                name: 'inOrder',
                component: InOrder  // 入库订单管理
            },
            {
                path: '/car/car',
                name: 'car',
                component: Car
            },
            {
                path: '/car/carRepair',
                name: 'carRepair',
                component: CarRepair
            },
            {
                path: '/cmn/cmn',
                name: 'cmn',
                component: Cmn
            },
            {
                path: '/order/order',
                name: 'order',
                component: Order
            },
            {
                path: '/order/orderHistory',
                name: 'orderHistory',
                component: OrderHistory
            },
            {
                path: '/dispatcher/carManage',
                name: 'carManage',
                component: CarManage
            },
            {
                path: '/dispatcher/orderPlat',
                name: 'orderPlat',
                component: OrderPlat
            },
            {
                path: '/dispatcher/personOrder',
                name: 'personOrder',
                component: PersonOrder
            },
            {
                path: '/dispatcher/historyOrder',
                name: 'historyOrder',
                component: HistoryOrder
            }
        ]
    },
    {
        path: '/',
        name: 'login',
        component: Login
    }
]

const router = new VueRouter({
    routes
})

router.beforeEach((to, from, next) => {
    // 做登录操作 直接放行
    if (to.path === '/') {
        next()
    }

    // 获取用户是否登录的 token
    let token = window.sessionStorage.getItem('token')

    // 没有登录 去做登录
    if (token === null || token === '') {
        next({
            path: '/',
            query: {redirect: to.fullPath}
        })
    } else next()
})

const VueRouterPush = VueRouter.prototype.push
VueRouter.prototype.push = function push(to) {
    return VueRouterPush.call(this, to).catch(err => err)
}

export default router
