import request from '@/utils/request'

const baseUrl = '/log/order/order'

export default {
    pageDisDispatcherOrder(page, limit) {  // 派送员查看当前自己正在执行的订单信息
        return request({
            url: `${baseUrl}/pageDisDispatcherOrder/${page}/${limit}`,
            method: 'get'
        })
    },

    updateOrderSure(id) {  // 确认订单送达
        return request({
            url: `${baseUrl}/updateOrderSure/${id}`,
            method: 'put'
        })
    },


    listOrderSure(orderIds) {   // 一次新确认多个订单送达
        return request({
            url: `${baseUrl}/listOrderSure`,
            method: 'put',
            data: orderIds
        })
    }
}
