import request from '@/utils/request'

const baseUrl = `/log/cmn/city`

export default {
    getAllCitiesByParentId(parentId) {  // 通过省级 id 获取市级信息
        return request({
            url: `${baseUrl}/getAllCitiesByParentId/${parentId}`,
            method: 'get'
        })
    },

    getCityById(id) { // 获取城市信息
        return request({
            url: `${baseUrl}/getCityById/${id}`,
            method: 'get'
        })
    }
}
