import request from '@/utils/request'

const baseUrl = `/log/order/order`

export default {
    pageDisNewOrder(page, limit) {   // 派送员分页查询新下单信息
        return request({
            url: `${baseUrl}/pageDisNewOrder/${page}/${limit}`,
            method: 'get'
        })
    },

    orderReceive(id) {    // 派送员接收订单
        return request({
            url: `${baseUrl}/orderReceive/${id}`,
            method: 'put'
        })
    },

    listOrderReceive(orderIds) {   // 一次下达多个订单
        return request({
            url: `${baseUrl}/listOrderReceive`,
            method: 'put',
            data: orderIds
        })
    }
}
