import request from '@/utils/request'

const baseUrl = '/log/cmn/province'

export default {
    getAllProvinces() {  // 获取所有省信息
        return request({
            url: `${baseUrl}/getAllProvinces`,
            method: 'get'
        })
    },

    getProvinceById(id) {  // 通过id获取省信息
        return request({
            url: `${baseUrl}/getProvinceById/${id}`,
            method: 'get'
        })
    }

}
