import request from '@/utils/request'

const baseUrl = `/log/car/car`

export default {
    pageFind(page, limit, carPageParams) {    // 分页查看当前车库当中车辆信息
        return request({
            url: `${baseUrl}/pageFind/${page}/${limit}`,
            method: 'post',
            data: carPageParams
        })
    },

    saveCar(car) {    // 保存车辆信息
        return request({
            url: `${baseUrl}/saveCar`,
            method: 'post',
            data: car
        })
    },

    removeCarById(id) {    // 单个删除车辆信息
        return request({
            url: `${baseUrl}/removeCarById/${id}`,
            method: 'delete'
        })
    },


    getCarById(id) {    // 获取车辆信息
        return request({
            url: `${baseUrl}/getCarById/${id}`,
            method: 'get'
        })
    },

    updateCar(car) {     // 更新车辆信息
        return request({
            url: `${baseUrl}/updateCar`,
            method: 'put',
            data: car
        })
    },

    dissolveRelationship(id) {    // 解除车辆和司机的关系
        return request({
            url: `${baseUrl}/dissolveRelationship/${id}`,
            method: 'put'
        })
    },


    relateCarAndDispatcher(carId, dispatcherId) {   // 添加车辆和司机的关系
        return request({
            url: `${baseUrl}/relateCarAndDispatcher/${carId}/${dispatcherId}`,
            method: 'put'
        })
    },

    listDeleteCars(carIds) {   // 一次移除多个车辆
        return request({
            url: `${baseUrl}/listDeleteCars`,
            method: 'delete',
            data: carIds
        })
    },

    getCarRequireNumber() {    // 获取司机申请的请求
        return request({
            url: `${baseUrl}/getCarRequireNumber`,
            method: 'get'
        })
    },

    getCarRequires() {     // 获取司机所有的请求车辆的信息
        return request({
            url: `${baseUrl}/getCarRequires`,
            method: 'get'
        })
    }

}
