import request from '@/utils/request'

const baseUrl = `/log/admin/admin`

export default {
    listAdminByRoleId(roleId) {
        return request({
            url: `${baseUrl}/listAdminByRoleId/${roleId}`,
            method: 'get'
        })
    },


    getPersonInfo() {  // 获取用户的个人信息
        return request({
            url: `${baseUrl}/getPersonInfo`,
            method: 'get'
        })
    },

    logout() {  // 退出登录
        return request({
            url: `${baseUrl}/logout`,
            method: 'post'
        })
    },

    updateInfo(updateAdmin) {  // 更新用户个人信息
        return request({
            url: `${baseUrl}/updateInfo`,
            method: 'put',
            data: updateAdmin
        })
    },

    updatePassByPass(params) {  // 通过密码方式更新密码
        return request({
            url: `${baseUrl}/updatePassByPass`,
            method: 'put',
            data: params
        })
    },

    updatePassByPhone(params) {  // 更新密码方式使用手机验证码
        return request({
            url: `${baseUrl}/updatePassByPhone`,
            method: 'put',
            data: params
        })
    }
}
