                component: InOrder  // 入库订单管理
            },
            {
                path: '/car/car',
                name: 'car',
                component: Car
            },
            {
                path: '/car/carRepair',
                name: 'carRepair',
                component: CarRepair
            },
            {
                path: '/cmn/cmn',
                name: 'cmn',
                component: Cmn
            },
            {
                path: '/order/order',
                name: 'order',
                component: Order
            },
            {
                path: '/order/orderHistory',
                name: 'orderHistory',
                component: OrderHistory
            },
            {
                path: '/dispatcher/carManage',
                name: 'carManage',
                component: CarManage
            },
            {
                path: '/dispatcher/orderPlat',
                name: 'orderPlat',
                component: OrderPlat
            },
            {
                path: '/dispatcher/personOrder',
                name: 'personOrder',
                component: PersonOrder
            },
            {
                path: '/dispatcher/historyOrder',
                name: 'historyOrder',
                component: HistoryOrder
            }
        ]
    },
    // {
    //     path: '/',
    //     name: 'login',
